import * as actionTypes from '../actions';

const initialstate = {
    favorite: [
        {
            favtitle: '',
            favedate: '',
            favimage: '',
            favdescription: '',
            favurl: ''
        }
    ],
    isFav: false
}
const reducer = (state=initialstate, action) => {
    switch(action.type){
        case actionTypes.TOGGLE_FAVORITE:
            return state.favorite.map(fav =>
                fav.payload === action.payload ? { ...fav, isFav: !fav.isFav } : ''
              )
        case actionTypes.ADD_NEW_FAVORITE:
            return{
                ...state,
                favorite: state.favorite.concat(action.payload),
            }               
         default:
             break;        
    }
    return state;
}

export default reducer;
