import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Container, Grid } from 'semantic-ui-react';
import Sources from '../components/SourceList/Sources';
import List from '../components/ListSection/List';
import Item from '../components/ItemSection/Item';
import classes from './Blog.module.css';
import * as actionTypes from '../store/actions';

class Blog extends Component {

render() {
   return (
    <div>
<Container>
 <h1 className={classes.projectname}>News Portal with React-Redux By Namit Dhupar</h1>
   <Grid container columns={3}>

   <Grid.Row>
    <Grid.Column width={4}>
      {/* {this.props.daily.map(dai =>(
       <Sources
       clicked={this.props.onSourceClick}
       /> 
      ))} */}
      <Sources
       clicked={this.props.onSourceClick}
       />
    </Grid.Column>

    <Grid.Column width={4}>
    {/* {this.props.dailyList.map(dl=> (
      <List
      clicklist={this.props.onNewsClick}/>  
    ))}  */}
    <List
      clicklist={this.props.onNewsClick}/> 
    </Grid.Column>

    <Grid.Column width={8}>
    {/* {this.props.dailyItem.map(di=> (
     <Item />  
    ))}  */}
      <Item />
    </Grid.Column>
    </Grid.Row> 

  </Grid>

 </Container>

  </div>
        )
    }
}

const mapStateToProps = state =>{
  return{
     daily: state.list.news,
     dailyList: state.list.newsList,
     dailyItem: state.item.newsItem
  };
};

const mapDispatchToProps = dispatch => {
  return{
    onSourceClick: () => dispatch({type: actionTypes.GET_SOURCE /*resultID: id*/}),
    onNewsClick: () => dispatch({type: actionTypes.GET_NEWS /*resultID: id*/})
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Blog);
