import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Card, Feed, Icon, Loader } from 'semantic-ui-react';
import classes from './List.module.css';
import * as actionCreators from '../../store/actions';
import InfiniteScroll from "react-infinite-scroll-component";

class List extends Component{

   componentDidMount(){
     this.props.onNewsLoad();
   }

  render(){
     let sourcename = null
     let newsList = null;
     if(this.props.dailySourceLoading){
       newsList = <div className={classes.loader}><Loader size='large' active inline='centered' /></div>;
       sourcename = <div className={classes.loader}><Loader size='medium' active inline='centered' /></div>;
     }
     if(!this.props.dailySourceLoading){
       newsList = ( this.props.dailySource.map((source,index) =>{
         return(
          <Feed.Event key={index}>
          <Feed.Label image={(source.urlToImage) ? source.urlToImage : "https://st2.depositphotos.com/1031343/6019/v/450/depositphotos_60193257-stock-illustration-news-flash-stamp.jpg"} />
          <Feed.Content className={classes.List}>
            <div className={classes.date}>
            <Feed.Date content={new Date(source.publishedAt).toDateString()} />
            </div>
            <div className={classes.star}>
            <Icon name='star' />
            </div>
            <Feed.Summary>
            {source.description}
            </Feed.Summary>
          </Feed.Content>
        </Feed.Event>
         )
       }))
       sourcename = ( this.props.dailySource.map(sn => 
        console.log(sn.source.name))) 
     }

    return(
  <Card fluid>
    <Card.Content>
      <Card.Header>
       <div className={classes.sourcehead}>

       </div> 
      </Card.Header>
    </Card.Content>
    <Card.Content>
    <InfiniteScroll dataLength={20} style={{height: "65vh" }}>
      <Feed>
       {newsList}
      </Feed>
      </InfiniteScroll>
    </Card.Content>
  </Card>
    )}
}

const mapStateToProps = state =>{
  return{
    dailySource: state.item.newsItem,
    dailySourceLoading: state.item.newsloading
  }
}

const mapDispatchToProps = dispatch =>{
  return{
    onNewsLoad: () => dispatch(actionCreators.getnews())
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(List);
