import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Container, Grid } from 'semantic-ui-react';
import Sources from '../components/SourceList/Sources';
import List from '../components/ListSection/List';
import Item from '../components/ItemSection/Item';
import classes from './Blog.module.css';

class Blog extends Component {
    state = {
        news: []
    }


render() {
   return (
    <div>
<Container>
 <h1 className={classes.projectname}>News Portal with React-Redux By Namit Dhupar</h1>
   <Grid container columns={3}>

   <Grid.Row>
    <Grid.Column width={4}>
      <Sources
       clicked={this.props.onSourceClick}
       /*src={this.props.daily.articles.name}*/ />
    </Grid.Column>

    <Grid.Column width={4}>
    {/* {this.props.dailyList.map(dl= (
      <List
      clicklist={this.props.onNewsClick}/>  
    ))}  */}
    <List/> 
    </Grid.Column>

    <Grid.Column width={8}>
    {/* {this.props.dailyList.map(dl= (
     <Item />  
    ))}  */}
      <Item />
    </Grid.Column>
    </Grid.Row> 

  </Grid>

 </Container>

  </div>
        )
    }
}

const mapStateToProps = state =>{
  return{
     daily: state.news,
     dailyList: state.newsList,
     dailyItem: state.newsItem
  };
};

const mapDispatchToProps = dispatch => {
  return{
    onSourceClick: () => dispatch({type: 'GET_SOURCE' /*resultID: id*/}),
    onNewsClick: () => dispatch({type: 'GET_NEWS' /*resultID: id*/})
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Blog);
