import React, {Component, Fragment} from 'react'
import { Card, Icon, Image, Loader } from 'semantic-ui-react';
import { connect } from 'react-redux';
import classes from './Item.module.css';

class Item extends Component{

  render(){
   // console.log(this.props.GetPostID);
    let newsPost = null;
    let newsImage = null;
    if(this.props.FullPostLoading){
      newsPost = <div className={classes.loader}><Loader size='large' active inline='centered' /></div>;
      newsImage = <div className={classes.loader}><Loader size='large' active inline='centered' /></div>;
    }
    if(!this.props.FullPostLoading){
      newsPost = ( this.props.FullPost.splice(1).slice(0,1).map((post, index) => {
        if(post.title === this.props.GetPostID){
        return (
        <Fragment>
        <Card.Header key={index}>{post.title}</Card.Header>
          <Card.Meta>
            <span className='date'>{new Date(post.publishedAt).toDateString()}</span>
          </Card.Meta>
          <Card.Description>
           {post.content}
          </Card.Description>
          </Fragment>
       )}
      })
      )

    }

   return(
    <Card fluid style={{height: "67.5vh" }}>
    <Image src='https://st2.depositphotos.com/1031343/6019/v/450/depositphotos_60193257-stock-illustration-news-flash-stamp.jpg' wrapped ui={false} />
    <Card.Content>
          {newsPost}
        </Card.Content>
        <Card.Content extra>
          <a>
            <Icon name='star' />
            Add To Favorites
          </a>
        </Card.Content>
    </Card>
 )
}
} 

const mapStateToProps = state =>{
  return{
    FullPost: state.item.newsPost,
    FullPostLoading: state.item.postloading,
    GetPostID: state.list.newsTitle
  }
}


export default connect(mapStateToProps, null)(Item)
