import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Card, Segment, Loader, Search } from 'semantic-ui-react';
import classes from './Sources.module.css';
import * as actionCreators from '../../store/actions';
import InfiniteScroll from "react-infinite-scroll-component";

class Sources extends Component{

  state = {
    search: ""
  }

componentDidMount(){
    this.props.onSourceLoad();
}

onSearch = (e) =>{
  //console.log(e.target.value);
  this.setState({search: e.target.value});
}

render(){

  let newsSource = null;

  //For Search
  let filteredSearch = this.props.dailyList.filter(res => {
    return res.name.indexOf(this.state.search) !== -1
  });
  newsSource =  filteredSearch.map(ds =>(      
    <p vertical key={ds.id}>
      <div className={classes.Source}>
      {ds.name}
      </div> 
    </p>
   ));

  //Getting List after ComponentDidMount
  if (this.props.dailyListLoading)
  newsSource = <div className={classes.loader}><Loader size='large' active inline='centered' /></div>;
  if(!this.props.dailyListLoading){
    newsSource = ( this.props.dailyList.map(dai =>(      
    <Segment vertical key={dai.id}>
      <div className={classes.Source}>
      {dai.name}
      </div> 
    </Segment>
   ))
  );
  }
return(
<div>
<Card fluid>
 <Card.Content>
   <Card.Header>
     <div className={classes.sourcehead}>
     News Sources
     </div>
     </Card.Header>
   <input type="text" value={this.state.search} onChange={this.onSearch.bind(this)} />
   <InfiniteScroll dataLength={20} style={{height: "65vh" }}>
     {newsSource}
     </InfiniteScroll>
    <Segment><b><i>Favorites(24)</i></b></Segment>
  </Card.Content> 
</Card>    
</div>
)
  }  
     }

const mapStateToProps = state =>{
  return{
      dailyList: state.list.newsList,
      dailyListLoading: state.list.loading
    // dailyItem: state.item.newsItem
  };
};

const mapDispatchToProps = dispatch => {
  return{
    onSourceLoad: () => dispatch(actionCreators.getsource())
    //onNewsClick: () => dispatch(actionCreators.getnews() /*resultID: id*/)
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Sources);
