import * as actionTypes from '../actions';

const initialstate = {
    favorite: []
}
const reducer = (state=initialstate, action) => {
    switch(action.type){
        case actionTypes.TOGGLE_FAVORITE:
            return{
                ...state,
                favorite: state.favorite.concat(action.payload)
            }                     
         default:
             break;        
    }
    return state;
}

export default reducer;
