import * as actionTypes from '../actions';

const initialstate = {
    isFavorite: 'false',
    unique: ''
}
const reducer = (state=initialstate, action) => {
    switch(action.type){
        case actionTypes.TOGGLE_FAVORITE:
            return{
                ...state,
                isFavorite: 'true',
                unique: action.payload
            }                     
         default:
             break;        
    }
    return state;
}

export default reducer;
