import React, {Component} from 'react'
import { Card, Icon, Image } from 'semantic-ui-react';
import { connect } from 'react-redux';
import InfiniteScroll from "react-infinite-scroll-component";

class Item extends Component{

render(){

return(
  <Card fluid>
    <Image fluid src={this.props.GetPostImage} size='massive' />
    <Card.Content>
      <Card.Header>{this.props.GetPostTitle}</Card.Header>
      <Card.Meta>
        <span className='date'>{this.props.GetPostDate}</span>
      </Card.Meta>
      <InfiniteScroll dataLength={10} style={{height: "67.5vh" }}>
      <Card.Description>
      {this.props.GetPostDescription}
      </Card.Description>
      </InfiniteScroll>
    </Card.Content>
    <Card.Content extra>
        <Icon name='star' />
        Add To Favorites
    </Card.Content>
  </Card>

 )
}
} 

const mapStateToProps = state =>{
  return{
    GetPostTitle: state.list.newsTitle,
    GetPostDescription: state.list.newsDescription,
    GetPostImage: state.list.newsImage,
    GetPostDate: state.list.newsDate    
  }
}


export default connect(mapStateToProps, null)(Item)
