import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Card, Segment, Loader, Search, Button, Icon, Label } from 'semantic-ui-react';
import classes from './Sources.module.css';
import * as actionCreators from '../../store/actions';
import InfiniteScroll from "react-infinite-scroll-component";

class Sources extends Component{

componentDidMount(){
    this.props.onSourceLoad();
}

render(){
    let newsSource = null;
  console.log(this.props.dailyListID)
  //Getting List after ComponentDidMount
  if (this.props.dailyListLoading)
  newsSource = <div className={classes.loader}><Loader size='large' active inline='centered' /></div>;
  if(!this.props.dailyListLoading){
    newsSource = ( this.props.dailyList.map((dai,index) =>{
    return(   
    <Segment vertical key={index}>
      <div className={classes.Source}>
      {dai.name}
      </div> 
    </Segment>
      );
    })
  );

  //For Search
  let filteredSearch = this.props.dailyList.filter(res => {
    return res.name.toLowerCase().indexOf(this.props.dailyListSearch.toLowerCase()) !== -1;
  });
  newsSource = filteredSearch.map((ds,index) =>(      
  <Segment vertical key={index}>
  <div className={classes.Source} onClick={()=>{this.props.getSourceID(ds.id);
  this.props.onNewsLoad(ds.id);}}>
      {ds.name}
      </div> 
    </Segment>
   ));
  }
return(
<div>
<Card fluid>
 <Card.Content>
   <Card.Header>
     <div className={classes.sourcehead}>
     News Sources
     </div>
     </Card.Header>
   <Search showNoResults={false} onSearchChange={(e)=>this.props.onSearchLoad(e.target.value)}/>
   <InfiniteScroll dataLength={this.props.dailyList.length} style={{height: "65vh" }}>
     {newsSource}
   </InfiniteScroll>
  </Card.Content> 
</Card>
<Button as='div' labelPosition='right'>
      <Button color='red'>
        <Icon name='star' />
        Favorites
      </Button>
      <Label as='a' basic color='red' pointing='left'>
        24
      </Label>
    </Button>      
</div>
)
  }  
     }

const mapStateToProps = state =>{
  return{
      dailyList: state.list.newsList,
      dailyListLoading: state.list.loading,
      dailyListSearch: state.list.search,
      dailyListID: state.list.id,
  };
};

const mapDispatchToProps = dispatch => {
  return{
    onSourceLoad: () => dispatch(actionCreators.getsource()),
    onSearchLoad: (payload) => dispatch(actionCreators.searchsource(payload)),
    getSourceID: (res) => dispatch(actionCreators.sourceid(res)),
    onNewsLoad: (id) => dispatch(actionCreators.getnews(id)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Sources);
