import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Card, Feed, Icon, Loader } from 'semantic-ui-react';
import classes from './List.module.css';
//import * as actionCreators from '../../store/actions';
import InfiniteScroll from "react-infinite-scroll-component";


class List extends Component{


 render(){
     //console.log(this.props.dailyListID);
     let sourcename = null;
     let newsList = null;
     if(this.props.dailySourceLoading){
       newsList = <div className={classes.loader}><Loader size='large' active inline='centered' /></div>;
       sourcename = <Loader size='medium' active inline='centered' />;
     }
     if(!this.props.dailySourceLoading){
       newsList = ( this.props.dailySource.map((source,index) =>{
         if(source.source.id === this.props.dailyListID){
         return(
          <Feed.Event key={index}>
          <Feed.Label image={(source.urlToImage) ? 
            source.urlToImage : 
          "https://st2.depositphotos.com/1031343/6019/v/450/depositphotos_60193257-stock-illustration-news-flash-stamp.jpg"} />
          <Feed.Content className={classes.List}>
            <div className={classes.date}>
            <Feed.Date content={new Date(source.publishedAt).toDateString()} />
            </div>
            <div className={classes.star}>
            <Icon name='star' />
            </div>
            <Feed.Summary>
            {source.description}
            </Feed.Summary>
          </Feed.Content>
        </Feed.Event>
         )
       } 
       }))
       //Extract the News Source Name
       sourcename = (this.props.dailySource.slice(0,1).map(sn =>{ 
        if(sn.source.id === this.props.dailyListID){
        Object.values(sn.source)[1];
        }
      } ));
     }

    return(
  <Card fluid>
    <Card.Content>
      <Card.Header>
       <div className={classes.sourcehead}>
         {sourcename}
       </div> 
      </Card.Header>
    </Card.Content>
    <Card.Content>
    <InfiniteScroll dataLength={this.props.dailySource.length} style={{height: "67.5vh" }}>
      <Feed>
       {newsList}
      </Feed>
      </InfiniteScroll>
    </Card.Content>
  </Card>
    )}
}

const mapStateToProps = state =>{
  return{
    dailySource: state.item.newsItem,
    dailySourceLoading: state.item.newsloading,
    dailyListID: state.list.id
  }
}

// const mapDispatchToProps = dispatch =>{
//   return{
//     onSourceID: (id) => dispatch(actionCreators.listsourceid(id))
//   }
// }

export default connect(mapStateToProps, null)(List);
