import * as actionTypes from '../actions';

const initialstate = {
    isFavorite: false
}
const reducer = (state=initialstate, action) => {
    switch(action.type){
        case actionTypes.TOGGLE_FAVORITE:
            return{

                isFavorite: !state.isFavorite
            }                     
         default:
             break;        
    }
    return state;
}

export default reducer;
