import React, {Component, Fragment} from 'react'
import { Card, Icon, Image, Loader } from 'semantic-ui-react';
import { connect } from 'react-redux';
//import classes from './Item.module.css';

class Item extends Component{

render(){

return(
  <Card fluid>
    <Image fluid src={this.props.GetPostImage} size='large' />
    <Card.Content>
      <Card.Header>{this.props.GetPostTitle}</Card.Header>
      <Card.Meta>
        <span className='date'>{this.props.GetPostDate}</span>
      </Card.Meta>
      <Card.Description>
      {this.props.GetPostDescription}
      </Card.Description>
    </Card.Content>
    <Card.Content extra>
      <a>
        <Icon name='star' />
        Add To Favorites
      </a>
    </Card.Content>
  </Card>

 )
}
} 

const mapStateToProps = state =>{
  return{
    GetPostTitle: state.list.newsTitle,
    GetPostDescription: state.list.newsDescription,
    GetPostImage: state.list.newsImage,
    GetPostDate: state.list.newsDate    
  }
}


export default connect(mapStateToProps, null)(Item)
