import * as actionTypes from '../actions';

const initialstate = {
    favorite: [
        {
            favtitle: '',
            favedate: '',
            favimage: '',
            favdescription: '',
            favurl: ''
        }
    ],
    isFav: false
}
const favreducer = (state=initialstate, action) => {
    switch(action.type){
        case actionTypes.ADD_NEW_FAVORITE:
            return{
                ...state,
                favorite: state.favorite.concat(action.payload),
                isFav: !state.isFav
            }                     
         default:
             break;        
    }
    return state;
}

export default favreducer;
