import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Card, Segment, Loader } from 'semantic-ui-react';
import classes from './Sources.module.css';
import * as actionCreators from '../../store/actions';
import InfiniteScroll from "react-infinite-scroll-component";

class Sources extends Component{

componentDidMount(){
    this.props.onSourceLoad();
}


render(){
  let newsSource = null;
  if (this.props.dailyListLoading)
  newsSource =  <Loader active inline='centered' />;
  if(!this.props.dailyListLoading){
    newsSource =( this.props.dailyList.map(dai =>(      
    <Segment vertical key={dai.id}>
      <div className={classes.Source}>
      {dai.name}
      </div> 
    </Segment>
   ))
  );
  }
return(
<div>
<Card>
 <Card.Content>
   <Card.Header>News Sources</Card.Header>
   <InfiniteScroll dataLength={20} height={470}>
     {newsSource}
     </InfiniteScroll>
    <Segment><b><i>Favorites(24)</i></b></Segment>
  </Card.Content> 
</Card>    
</div>
)
  }  
     }

const mapStateToProps = state =>{
  return{
      dailyList: state.list.newsList,
      dailyListLoading: state.list.loading
    // dailyItem: state.item.newsItem
  };
};

const mapDispatchToProps = dispatch => {
  return{
    onSourceLoad: () => dispatch(actionCreators.getsource())
    //onNewsClick: () => dispatch(actionCreators.getnews() /*resultID: id*/)
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Sources);
