import * as actionTypes from '../actions';

const initialstate = {
    isFavorite: false,
    favoriteID: ''
}
const reducer = (state=initialstate, action) => {
    switch(action.type){
        case actionTypes.TOGGLE_FAVORITE:
            return{
                ...state,
                isFavorite: !state.isFavorite,
                favoriteID: action.payload
            }                     
         default:
             break;        
    }
    return state;
}

export default reducer;
