import React, {Component} from 'react'
import { Card, Icon, Image, Loader } from 'semantic-ui-react';
import { connect } from 'react-redux';
import classes from './Item.module.css';

class Item extends Component{

  render(){
   let newsPost = null;
   if(this.props.FullPostLoading){
    newsPost = <div className={classes.loader}><Loader size='large' active inline='centered' /></div>;
  }

   return(
    <Card fluid>
    <Image src='https://st2.depositphotos.com/1031343/6019/v/450/depositphotos_60193257-stock-illustration-news-flash-stamp.jpg' wrapped ui={false} />
    <Card.Content>
          <Card.Header>OK</Card.Header>
          <Card.Meta>
            <span className='date'>22/09/1996 </span>
          </Card.Meta>
          <Card.Description>
           Card Description
          </Card.Description>
        </Card.Content>
        <Card.Content extra>
          <a>
            <Icon name='star' />
            Add To Favorites
          </a>
        </Card.Content>
    </Card>
 )
}
} 

const mapStateToProps = state =>{
  return{
    FullPost: state.item.newsPost,
    FullPostLoading: state.item.postloading
  }
}


export default connect(mapStateToProps, null)(Item)
