import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Card, Feed, Icon, Loader } from 'semantic-ui-react';
import classes from './List.module.css';
import * as actionCreators from '../../store/actions';
import InfiniteScroll from "react-infinite-scroll-component";

class List extends Component{

   componentDidMount(){
     this.props.onNewsLoad();
   }

  render(){
     let newsList = null;
     if(this.props.dailySourceLoading){
       newsList = <div className={classes.loader}><Loader size='large' active inline='centered' /></div>;
     }

    return(
  <Card fluid>
    <Card.Content>
      <Card.Header>
       <div className={classes.sourcehead}>
         Source Name
       </div> 
      </Card.Header>
    </Card.Content>
    <Card.Content>
    <InfiniteScroll dataLength={20} style={{height: "65vh" }}>
      <Feed>
       <Feed.Event>
          <Feed.Label image='https://www.kindpng.com/picc/m/172-1724396_dragon-of-dojima-tattoo-hd-png-download.png' />
          <Feed.Content className={classes.List}>
            <div className={classes.date}>
            <Feed.Date content='1 day ago' />
            </div>
            <div className={classes.star}>
            <Icon name='star' />
            </div>
            <Feed.Summary>
              You added Jenny Hess to your coworker group.
            </Feed.Summary>
          </Feed.Content>
        </Feed.Event>
      </Feed>
      </InfiniteScroll>
    </Card.Content>
  </Card>
    )}
}

const mapStateToProps = state =>{
  return{
    dailySource: state.item.newsItem,
    dailySourceLoading: state.item.newsloading
  }
}

const mapDispatchToProps = dispatch =>{
  return{
    onNewsLoad: () => dispatch(actionCreators.getnews())
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(List);
